kickstart.post:
DEVICE=eth0 BOOTPROTO=dhcp ONBOOT=yes
NETWORKING=yes
if [ -d /install ]; then /bin/rm -rf /install fi
EC2-enabled Client
#!/bin/sh # $Id: ec2-client.xml,v 1.17 2012/09/24 18:47:08 clem Exp $ # # chkconfig: 2345 60 40 # description: ec2 ssh key access # . /etc/rc.d/init.d/functions
RETVAL=0
copykeys() { if [ ! -d /root/.ssh ] ; then mkdir -p /root/.ssh chmod 700 /root/.ssh fi # Fetch public key using HTTP curl http://169.254.169.254/2009-04-04//meta-data/public-keys/0/openssh-key > /tmp/my-key 2>/dev/null while [ "$?" -ne "0" ] ; do sleep 2 curl http://169.254.169.254/2009-04-04//meta-data/public-keys/0/openssh-key > /tmp/my-key 2>/dev/null done
if [ -f /tmp/my-key ] ; then cat /tmp/my-key >> /root/.ssh/authorized_keys chmod 700 /root/.ssh/authorized_keys rm /tmp/my-key return 0 else return 1 fi }
function shiftChar(){ # # this function shift the input character $1 of 4 # position (redhat device naming madness) # https://bugzilla.redhat.com/show_bug.cgi?id=729586 # sda become xvd(a + 4) # intCharInitial=`printf "%d" \'$1` #from int to char intCharFinal=`expr $intCharInitial + 4` charFinal=`printf "\x$(printf %x $intCharFinal)"` echo -n $charFinal }
function setMount(){ # # update the fstab with ephemeral0 storage # which change device based on instance type # http://docs.amazonwebservices.com/AWSEC2/latest/UserGuide/InstanceStorage.html#InstanceStoreDeviceNames # OrginalDevice=`curl http://169.254.169.254/2009-04-04/meta-data/block-device-mapping/ephemeral0` NewDevName=xvd`shiftChar ${OrginalDevice:2:1}` if [ ${#OrginalDevice} == 4 ]; then #we need the partition number NewDevName=$NewDevName${OrginalDevice:3:1} fi echo New device name is $NewDevName if ! grep $NewDevName /etc/fstab ; then cp /etc/fstab /etc/fstab.orig echo "/dev/$NewDevName /mnt auto defaults,comment=rocksconfig 0 2" >> /etc/fstab
if [ -b /dev/xvde3 ]; then echo "/dev/xvde3 none swap sw,comment=rocksconfig 0 0" >> /etc/fstab swapon -a fi mount -a fi
}
case "$1" in start) echo -n "Adding User-Supplied EC2 Root SSH Key" copykeys setMount RETVAL=$? echo [ $RETVAL -eq 0 ] ;;
stop) echo -n "EC2 Root SSH Key " [ $RETVAL -eq 0 ] ;;
restart|reload) $0 stop $0 start RETVAL=$? ;; *) echo "Usage: $0 {start|stop|restart}" exit 1 esac
exit $RETVAL
chkconfig --add ec2-root-ssh
if [ ! -d /mnt/ec2image ]; then mkdir -p /mnt/ec2image fi
#disablying rocks-grub /etc/init.d/rocks-grub stop /sbin/chkconfig rocks-grub off
