kickstart.post:
#!/bin/sh # # chkconfig: 2345 60 40 # description: ec2 postinstall functions # . /etc/rc.d/init.d/functions
RETVAL=0
runpostinstall() { # # Fetch the new kickstart file #a
LOGFILE=/var/log/postinstall.log URL="https://137.110.119.100/install/sbin/EC2kickstart.cgi?arch=x86_64&np=1" echo Downloading kickstart from frontend $URL > $LOGFILE curl --insecure "$URL" > /root/postinstall.xml 2>> $LOGFILE while [ "$?" -ne "0" ] ; do sleep 2 curl --insecure "$URL" > /root/postinstall.xml 2>> $LOGFILE done
cat /root/postinstall.xml | rocks list host profile > /root/postinstall.ks rocks run ec2 postinstall /root/postinstall.ks 2>> $LOGFILE
}
case "$1" in start) echo -n "Downloading kickstart file..." runpostinstall RETVAL=$? echo [ $RETVAL -eq 0 ] ;;
stop) echo -n "Retrive kickstart file " [ $RETVAL -eq 0 ] ;;
restart|reload) $0 stop $0 start RETVAL=$? ;; *) echo "Usage: $0 {start|stop|restart}" exit 1 esac
exit $RETVAL
chkconfig --add ec2-postinstall
